function LOGO() {
    document.getElementById('LAPMELON').style.width = '36px';
    document.getElementById('LAPMELON').style.height = '29px';
    document.getElementById('LAPMELON').style.transition = '0.5s';
}
function LOGOUP() {
    document.getElementById('LAPMELON').style.width = '30px';
    document.getElementById('LAPMELON').style.height = '24px';
    document.getElementById('LAPMELON').style.transition = '0.5s';
}
function infolist() {
    document.getElementById('infolist').style.backgroundColor = 'palevioletred'
    document.getElementById('infolist').style.transition = '0.5s';
    document.getElementById('infolist').style.letterSpacing = '0.5px'
}
function infolistout() {
    document.getElementById('infolist').style.backgroundColor = 'rgb(223, 163, 183)'
    document.getElementById('infolist').style.letterSpacing = '0px'
    document.getElementById('infolist').style.transition = '0.5s';
}
